import React,{useState} from 'react';
import './App.css';

function App() {

  //let [titles,contents] = useState(['글제목1','글제목2','글제목3'],['글내용1','글내용2','글내용3']);

  let boardList = [
    {
      title:'1',
      date:'2022/08/19',
      content:'111111'
    },
    {
      title:'2',
      date:'2022/08/19',
      content:'22222'
    },
    {
      title:'3',
      date:'2022/08/19',
      content:'3333333'
    },
  ];

  return (
    <div className="App">
      <div className="black-nav">
        <div>개발 Blog</div>        
      </div>
      <div className='board_list'>
        <Board boardInfo = {boardList[0]} />
        <Board boardInfo = {boardList[1]} />
        <Board boardInfo = {boardList[2]} />
        {boardList.map((b,i)=>{
           <Board boardInfo = {b[i]} />
        })}

      </div>
    </div>
  );
}

function Board(prop){
  return (
    <div className='board'>
      <h2>{prop.boardInfo.title}</h2>
      <p>{prop.boardInfo.date}</p>
      <p>{prop.boardInfo.content}</p>
    </div>
  );
}

export default App;
